const aiOutput = $input.item.json;
const webhookData = $items('Prepare Input')[0].json.webhookData;

let parsed;
try {
  let text = aiOutput.output || aiOutput.text || JSON.stringify(aiOutput);
  text = text.trim().replace(/```json\n?/g, '').replace(/```\n?/g, '');
  const jsonMatch = text.match(/\{[\s\S]*\}/);
  parsed = JSON.parse(jsonMatch ? jsonMatch[0] : text);
  if (!parsed.contacts || !Array.isArray(parsed.contacts)) {
    throw new Error('Missing contacts');
  }
} catch (e) {
  return [{
    json: {
      user_id: webhookData.user_id,
      campaign_id: webhookData.campaign_id,
      company_research_id: webhookData.company_research_id,
      company_domain: webhookData.company_domain,
      salesforce_account_id: webhookData.salesforce_account_id,
      salesforce_campaign_id: webhookData.salesforce_campaign_id,
      prospect: {
        status: 'error',
        company: webhookData.company.name,
        contacts: []
      }
    }
  }];
}

const validContacts = parsed.contacts.filter(c => c.first_name && c.last_name && c.linkedin && c.linkedin.startsWith('http'));

if (validContacts.length === 0) {
  return [{
    json: {
      user_id: webhookData.user_id,
      campaign_id: webhookData.campaign_id,
      company_research_id: webhookData.company_research_id,
      company_domain: webhookData.company_domain,
      salesforce_account_id: webhookData.salesforce_account_id,
      salesforce_campaign_id: webhookData.salesforce_campaign_id,
      prospect: {
        status: 'error',
        company: webhookData.company.name,
        contacts: []
      }
    }
  }];
}

return [{
  json: {
    user_id: webhookData.user_id,
    campaign_id: webhookData.campaign_id,
    company_research_id: webhookData.company_research_id,
    company_domain: webhookData.company_domain,
    salesforce_account_id: webhookData.salesforce_account_id,
    salesforce_campaign_id: webhookData.salesforce_campaign_id,
    prospect: {
      status: parsed.status || 'completed',
      company: parsed.company || webhookData.company.name,
      contacts: validContacts
    }
  }
}];

const body = $json.body;

const companyName = body.company?.name || '';
const companyWebsite = body.company?.website || '';
const companyDomain = companyWebsite.replace(/^https?:\/\//, '').replace(/^www\./, '').split('/')[0];

// Target titles - THE PRIMARY FILTER
const targetTitlesRaw = body.campaign?.targetTitles || '';
const targetTitlesArray = targetTitlesRaw.split('\n').map(t => t.trim()).filter(t => t.length > 0);
const targetTitles = targetTitlesArray.slice(0, 15).join(', ');

// Top 3 for focused searches
const topTitles = targetTitlesArray.slice(0, 3).join(', ');

const painPointsRaw = body.campaign?.painPoints || '';
const painPointsArray = painPointsRaw.split('\n').filter(p => p.trim().length > 15);
const painPoints = painPointsArray.slice(0, 5).join('; ');

const techFocus = body.campaign?.techFocus || '';
const region = body.campaign?.targetRegion || '';
const companySummary = body.companyResearch?.summary || '';

const chatInput = `ðŸŽ¯ TARGET TITLES (ONLY FIND PEOPLE WITH THESE TITLES):
${targetTitles}

Top Priority: ${topTitles}

Find 3-7 IT/Cloud decision-makers at ${companyName} whose titles match the list above.

COMPANY:
- Name: ${companyName}
- Website: ${companyWebsite}
- Domain: ${companyDomain}
- Region: ${region}
- Industry: ${companySummary}

WHAT WE'RE SELLING:
- Product: ${body.campaign?.product || ''}
- Value: ${body.campaign?.primaryAngle || ''}
- Tech Focus: ${techFocus}
- Pain Points: ${painPoints}

SEARCH STRATEGY:
1. Search web for: "${companyName}" CTO, "${companyName}" Head of IT, "${companyName}" Cloud Architect
2. Find REAL people with full names
3. Verify their title matches Target Titles list
4. For each match, call search_linkedin tool to get LinkedIn URL
5. Only include people whose titles match Target Titles

ðŸš« DO NOT RETURN: HR, Sales, Marketing, Finance, Operations (unless listed in Target Titles)
âœ… ONLY RETURN: People whose titles match Target Titles list

Return JSON with contacts.`;

return [{
  json: {
    chatInput: chatInput,
    webhookData: body
  }
}];
