// Parse AI Agent output
const aiOutput = $input.item.json;
const webhookData = $items('Prepare Input')[0].json.webhookData;

let parsed;
try {
  let text = aiOutput.output || aiOutput.text || JSON.stringify(aiOutput);

  // Remove markdown code blocks
  text = text.trim().replace(/```json\n?/g, '').replace(/```\n?/g, '');

  // Extract JSON object
  const jsonMatch = text.match(/\{[\s\S]*\}/);
  if (!jsonMatch) {
    throw new Error('No JSON found in output');
  }

  parsed = JSON.parse(jsonMatch[0]);

  if (!parsed.contacts || !Array.isArray(parsed.contacts)) {
    throw new Error('Missing contacts array');
  }
} catch (e) {
  console.error('Parse error:', e.message);
  return [{
    json: {
      user_id: webhookData.user_id,
      campaign_id: webhookData.campaign_id,
      company_research_id: webhookData.company_research_id,
      company_domain: webhookData.company_domain,
      salesforce_account_id: webhookData.salesforce_account_id,
      prospect: {
        status: 'error',
        company: webhookData.company.name,
        contacts: []
      }
    }
  }];
}

// Validate and clean contacts
const validContacts = parsed.contacts.filter(c => {
  // Must have required fields
  if (!c.first_name || !c.last_name || !c.job_title) {
    return false;
  }

  // LinkedIn URL validation (can be empty, but if present must be valid)
  if (c.linkedin && c.linkedin.length > 0) {
    if (!c.linkedin.startsWith('http')) {
      return false;
    }
    if (!c.linkedin.includes('/in/')) {
      return false;
    }
  }

  return true;
}).map(c => {
  // Ensure all fields are present
  return {
    first_name: c.first_name,
    last_name: c.last_name,
    job_title: c.job_title,
    linkedin: c.linkedin || '',
    priority: c.priority || 'Medium',
    priority_reason: c.priority_reason || '',
    pitch_type: c.pitch_type || 'Technical Leader',
    confidence_score: c.confidence_score || 50,
    cloud_alignment: c.cloud_alignment || 'Unknown',
    evidence: c.evidence || 'No evidence provided'
  };
});

return [{
  json: {
    user_id: webhookData.user_id,
    campaign_id: webhookData.campaign_id,
    company_research_id: webhookData.company_research_id,
    company_domain: webhookData.company_domain,
    salesforce_account_id: webhookData.salesforce_account_id,
    prospect: {
      status: parsed.status || 'completed',
      company: parsed.company || webhookData.company.name,
      contacts: validContacts
    }
  }
}];
