const body = $json.body;

// Extract campaign data
const targetTitlesArray = body.campaign?.targetTitles || [];
const targetTitles = Array.isArray(targetTitlesArray) 
  ? targetTitlesArray.slice(0, 15).join('\n- ')
  : targetTitlesArray;

const painPointsArray = body.campaign?.painPoints || [];
const painPoints = Array.isArray(painPointsArray)
  ? painPointsArray.filter(p => p.trim().length > 10).slice(0, 8).join('\n- ')
  : painPointsArray;

const personasArray = body.campaign?.targetPersonas || [];
const personas = Array.isArray(personasArray)
  ? personasArray.slice(0, 5).join('\n- ')
  : personasArray;

return [{
  json: {
    ...body,
    formatted_titles: targetTitles,
    formatted_pain_points: painPoints,
    formatted_personas: personas
  }
}];

