const aiOutput = $input.item.json;
const webhookData = $items('Prepare Campaign Data')[0].json;

// Parse AI output
let parsed;
try {
  let text = aiOutput.output || aiOutput.text || JSON.stringify(aiOutput);
  text = text.trim().replace(/```json\n?/g, '').replace(/```\n?/g, '');
  const jsonMatch = text.match(/\{[\s\S]*\}/);
  parsed = JSON.parse(jsonMatch ? jsonMatch[0] : text);
  if (!parsed.contacts) throw new Error('Missing contacts');
} catch (e) {
  console.error('Parse error:', e);
  return [{
    json: {
      status: 'error',
      company: webhookData.company?.name || 'Unknown',
      contacts: []
    }
  }];
}

// Enhanced fuzzy matching for job titles
const matchesTargetTitles = (jobTitle, targetTitles) => {
  if (!jobTitle || !targetTitles) return false;

  const normalizedTitle = jobTitle.toLowerCase();
  const campaignTitles = Array.isArray(targetTitles) 
    ? targetTitles.map(t => t.toLowerCase())
    : targetTitles.split('\n').map(t => t.trim().replace(/^-\s*/, '').toLowerCase()).filter(t => t);

  // Exact match
  if (campaignTitles.some(t => normalizedTitle === t)) return true;

  // Fuzzy keyword match with 60% threshold
  return campaignTitles.some(campaignTitle => {
    // Extract meaningful keywords (min 3 chars, exclude common words)
    const stopWords = ['the', 'and', 'for', 'of', 'with', 'at'];
    const campaignKeywords = campaignTitle
      .split(/[\s,\-\/\(\)]+/)
      .filter(k => k.length > 2 && !stopWords.includes(k));

    const titleKeywords = normalizedTitle
      .split(/[\s,\-\/\(\)]+/)
      .filter(k => k.length > 2);

    // Count matching keywords (including partial matches)
    const matchCount = campaignKeywords.filter(ck => 
      titleKeywords.some(tk => tk.includes(ck) || ck.includes(tk))
    ).length;

    // Match if 60%+ keywords overlap
    return matchCount >= Math.ceil(campaignKeywords.length * 0.6);
  });
};

// Enhanced priority calculation
const calculatePriority = (contact, campaign) => {
  const title = (contact.job_title || '').toLowerCase();
  const techFocus = (campaign?.techFocus || '').toLowerCase();
  const product = (campaign?.product || '').toLowerCase();

  // C-level (High priority)
  if (/\b(cto|cio|ciso|chief|vp|vice president)\b/.test(title)) {
    return {
      priority: 'High',
      reason: `${contact.job_title} - Executive decision-maker with budget authority for ${product || techFocus} initiatives`,
      pitch_type: 'Executive'
    };
  }

  // Director/Head (Medium priority)
  if (/\b(director|head of|head\s)\b/.test(title)) {
    return {
      priority: 'Medium',
      reason: `${contact.job_title} - Strategic influencer for ${techFocus || 'cloud modernization'} decisions`,
      pitch_type: 'Executive'
    };
  }

  // Manager/Lead/Architect (Medium priority)
  if (/\b(manager|lead|architect|specialist|principal)\b/.test(title)) {
    return {
      priority: 'Medium',
      reason: `${contact.job_title} - Operational owner who implements ${techFocus || product} solutions`,
      pitch_type: 'Technical'
    };
  }

  return {
    priority: contact.priority || 'Low',
    reason: contact.priority_reason || `${contact.job_title} - Relevant technical role`,
    pitch_type: contact.pitch_type || 'Technical'
  };
};

// Validate LinkedIn URL
const validateLinkedInUrl = (url) => {
  if (!url || typeof url !== 'string') return null;

  let cleaned = url.trim();
  if (!cleaned) return null;

  // Add https:// if missing
  if (cleaned.startsWith('linkedin.com') || cleaned.startsWith('www.linkedin.com')) {
    cleaned = 'https://' + cleaned;
  }

  // Must be a LinkedIn profile URL
  if (!cleaned.includes('linkedin.com/in/')) return null;

  // Remove trailing slash and query params
  cleaned = cleaned.split('?')[0].replace(/\/$/, '');

  return cleaned;
};

// Process contacts with LENIENT LinkedIn filtering for High priority
const processedContacts = parsed.contacts
  .map(c => {
    // Calculate priority first
    const priorityData = calculatePriority(c, webhookData.campaign);

    // Validate LinkedIn URL
    const linkedinUrl = validateLinkedInUrl(c.linkedin);
    const needsFallback = !linkedinUrl || c.needs_serpapi_fallback === true;

    // ✅ Allow High priority contacts without LinkedIn (they'll get SerpAPI fallback)
    // ❌ Filter Medium/Low priority contacts without LinkedIn
    if (!linkedinUrl && priorityData.priority !== 'High') {
      console.log(`Filtered: ${c.first_name} ${c.last_name} - no LinkedIn URL (priority: ${priorityData.priority})`);
      return null;
    }

    // Check campaign match
    const matchesCampaign = matchesTargetTitles(c.job_title, webhookData.campaign?.targetTitles);
    if (!matchesCampaign) {
      console.log(`Filtered: ${c.first_name} ${c.last_name} (${c.job_title}) - doesn't match campaign titles`);
      return null;
    }

    return {
      first_name: c.first_name,
      last_name: c.last_name,
      job_title: c.job_title,
      linkedin: linkedinUrl || null,
      priority: priorityData.priority,
      priority_reason: priorityData.reason,
      pitch_type: priorityData.pitch_type,
      linkedin_source: c.linkedin_source || 'serper_ai_agent',
      needs_serpapi_fallback: needsFallback
    };
  })
  .filter(c => c !== null)
  .sort((a, b) => {
    const priorityOrder = { 'High': 3, 'Medium': 2, 'Low': 1 };
    return (priorityOrder[b.priority] || 0) - (priorityOrder[a.priority] || 0);
  });

// Quality threshold: Cap at 10 contacts to maintain quality
const validContacts = processedContacts.slice(0, 10);

console.log(`Campaign: ${webhookData.campaign?.campaignName || 'Unknown'}`);
console.log(`Company: ${webhookData.company?.name} (${webhookData.companyResearch?.cloud_preference?.provider || 'Unknown'})`);
console.log(`Valid contacts: ${validContacts.length}/${parsed.contacts.length}`);
console.log(`High priority: ${validContacts.filter(c => c.priority === 'High').length}`);
console.log(`Needs LinkedIn fallback: ${validContacts.filter(c => c.needs_serpapi_fallback).length}`);

return [{
  json: {
    status: validContacts.length > 0 ? 'completed' : 'no_matches',
    company: parsed.company || webhookData.company?.name,
    contacts: validContacts,
    campaign_match_summary: {
      total_found: parsed.contacts.length,
      campaign_matched: validContacts.length,
      high_priority: validContacts.filter(c => c.priority === 'High').length,
      medium_priority: validContacts.filter(c => c.priority === 'Medium').length,
      needs_linkedin_fallback: validContacts.filter(c => c.needs_serpapi_fallback).length
    }
  }
}];

