={{ 
  // Extract input from AI in format: "FirstName|LastName|CompanyName"
  const input = $fromAI('text', 'Provide: FirstName|LastName|CompanyName (pipe-separated)', 'text');
  const parts = input.split('|').map(s => s.trim());
  const firstName = parts[0] || '';
  const lastName = parts[1] || '';
  const company = parts[2] || '';

  // Build simple query WITHOUT site: operators (Serper blocks these)
  const query = `"${firstName} ${lastName}" "${company}" linkedin profile`;

  return JSON.stringify({
    q: query,
    num: 5
  });
}}

